interface Config {
  apiUrl: string;
  environment: 'development' | 'production';
}

const config: Config = {
  apiUrl: process.env.NODE_ENV === 'production' 
    ? 'https://scheduling-gpt-assistant.herokuapp.com/api/chat'  // Heroku backend URL
    : 'http://localhost:5001/api/chat',  // Local development URL
  environment: process.env.NODE_ENV as 'development' | 'production',
};

export default config;
